def add(num1,num2):
    total = str(num1 + num2)
    return total
def sub(num1,num2):
    total = str(num1 - num2)
    return total
def mul(num1,num2):
    total = str(num1 * num2)
    return total
def div(num1,num2):
    total = str(num1 / num2)
    return total
