from decimal import Decimal


# ┌────────────────────────────────────────────────────────────────────────────────────┐
# │ TO DECIMAL                                                                         │
# └────────────────────────────────────────────────────────────────────────────────────┘


def to_decimal(value):
    """ Converts a value to Decimal """

    # Return Decimal object
    return Decimal(str(value))
