"""
Some math functions.
"""


def add(a_num, b_num):
    """Adds two numbers."""
    return a_num + b_num


def sub(a_num, b_num):
    """Subtract two numbers."""
    return a_num - b_num


def mul(a_num, b_num):
    """Subtract two numbers."""
    return a_num * b_num

def div(a_num, b_num):
    """Subtract two numbers."""
    return a_num / b_num